mapper.update:
update hw_submit set 
HW_SUBMIT_SCORE = #{hwSubmitScore}
where HW_SUBMIT_WRITER = #{hwSubmitWriter} and BOARD_NUM = #{boardNum}

mapper.select:
select HW_SUBMIT_WRITER, HW_SUBMIT_SCORE, HW_SUBMIT, HW_SUBMIT_GROUP_NUM, MEM_NAME
from HW_SUBMIT join MEM
on HW_SUBMIT_WRITER = MEM_NAME and HW_SUBMIT_GROUP_NUM = #{hwSubmitGroupNum}
