mapper.select:
SELECT
	*
FROM
	ATTENDED
WHERE
	ATTENDED_NUM = (SELECT MAX(ATTENDED_NUM) FROM ATTENDED WHERE GROUP_NUM = #{groupNum} 
		AND MEM_EMAIL = #{memEmail}
		AND TO_CHAR(ATTENDED_IN,'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD') )
	AND GROUP_NUM = #{groupNum} 
	AND MEM_EMAIL = #{memEmail}
	AND TO_CHAR(ATTENDED_IN,'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD')
